import React,    { useState,useEffect } from 'react';
import Routers from './routers'
import api from './api/productApi'

import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";
function App() {
  const [products, setProducts] = useState([]);
  useEffect(() => {
    const getProducts = async () => {
      try {
        const { data } = await api.getAll();
        setProducts(data);
      } catch (error) {
        console.log('failed to request API: ', error)
      }
    }
    getProducts();

  },
   []);
  const [status, setStatus] = useState(false);
  const [color, setColor] = useState('green');
  const onHandleClick = () => {
    // setStatus(true);
    setColor('red');
  }
  const onHandleRemove = (id) => {
    const newProducts = products.filter(product => product.id !== id);
    setProducts(newProducts);
  }
  const onHandleAdd = async (product) => {
    try {
      const { data } = await api.create(product);
      setProducts([
        ...products,
        data
      ])
    } catch (error) {
      console.log('failed to request API: ', error)
    }
  }
  const onHandleEdit = (id, value_edit) => {
    const newProducts = [...products];
    newProducts.forEach((element, index) => {
      if (element.id == id) newProducts.splice(index, 1);
    });
    setProducts([...newProducts, value_edit]);
  };
  const onHandleUpdate = (updateProduct) => {
    const newProducts = products.map(product => (
      product.id === updateProduct.id ? updateProduct : product  // Nếu product.id bằng với id của sản phẩm vừa chỉnh sửa thì trả về mảng có object mới
    ));
    localStorage.setItem('products', JSON.stringify(newProducts))
    setProducts(newProducts);
  }
  return (
    <div className="App">
      <Routers products={products} onRemove={onHandleRemove} onAdd={onHandleAdd} onUpdate={onHandleUpdate}/>
    </div>
  )

}
export default App;
