import React, { useState } from 'react'
import PropTypes from 'prop-types'
import { useHistory } from 'react-router-dom';



function AddProduct({ onAdd }) {
    let history = useHistory();
    const [valueInput, setValueInput] = useState({});
    function onHandleChange(e) {
        const { name, image, price, value } = e.target;
        setValueInput({
            ...valueInput,
            [name]: e.target.value,
            [image]: e.target.value,
            [price]: e.target.value,
        })
    }
    function onSubmit(e) {
        e.preventDefault();
        onAdd(valueInput);
        history.push("/admin/products");
    }

    return (
        <div>
            <section class="content">
                <h3 className="card-title">Thêm sản phẩm</h3>
                <form >
                    <div className="form-group">
                        <label htmlFor="exampleInputEmail1">Tên sản phẩm</label>
                        <input type="text"
                            name="name"
                            className="form-control"
                            id="inputProductName"
                            onChange={onHandleChange} aria-describedby="emailHelp" />
                    </div>
                    <div className="form-group">
                        <label htmlFor="exampleInputEmail1">Gía sản phẩm</label>
                        <input type="text"
                            name="price"
                            className="form-control"
                            id="inputProductName"
                            onChange={onHandleChange} aria-describedby="emailHelp" />
                    </div>
                    <div className="form-group">
                        <label htmlFor="exampleInputEmail1">Ảnh sản phẩm</label>
                        <input type="text"
                            name="image"
                            className="form-control"
                            id="inputProductName"
                            onChange={onHandleChange} aria-describedby="emailHelp" />
                    </div>
                    <button onClick={onSubmit} type="submit" className="btn btn-primary">Submit</button>
                </form>
            </section>
        </div>
    )
}

export default AddProduct;
